import React, { useEffect, useState } from "react";
import Sidebar from "../components/Sidebar";
import Menu from "../components/Menu";
import TagNav from "../components/TagNav";

function Home() {
  const [tag, setTag] = useState("");
  const [token, setToken] = useState("");
  const [classes, setClass] = useState([]);
  const [pass, setPass] = useState(0);
  const [left, setLeft] = useState(0);

  const url = "https://api.trungthanhweb.com/api/";
  const setSingleClass = (id) => {
    classes.forEach((el) => {
      if (el.id == id) {
        setPass(Number(el.pass));
        setLeft((Number(el.duration)-Number(el.pass)));
      }
    });
  };
  useEffect(() => {
    setTag("Home");
    if (localStorage.getItem("token")) {
      setToken(localStorage.getItem("token"));
    } else {
      window.location.replace("/");
    }
  }, []);
  useEffect(() => {
    fetch(url + "getClassListTeacher?token=" + token)
      .then((res) => res.json())
      .then((res) => setClass(res));
  }, [token]);
  return (
    <>
      <Sidebar />
      <section className="main_content dashboard_part large_header_bg">
        <Menu />
        <div className="main_content_iner overly_inner">
          <TagNav name={tag} />
          {left== 0 && (
            <div className="table-responsive">
              <table className="table table-success">
                <thead>
                  <tr>
                    <th scope="col">Lớp</th>
                  </tr>
                </thead>
                <tbody>
                  {classes.length > 0 &&
                    classes.map((item, index) => (
                      <tr key={index} className>
                        <td scope="row">
                          <a href="#" onClick={(e) => setSingleClass(item.id)}>
                            {item.name}
                          </a>
                        </td>
                      </tr>
                    ))}
                </tbody>
              </table>
            </div>
          )}
          <div className="row mt-5">
            <b>Số buổi học</b>
        </div>
        </div>
      </section>
    </>
  );
}

export default Home;
