import React, { useEffect, useState } from "react";
import Sidebar from "../components/Sidebar";
import Menu from "../components/Menu";
import TagNav from "../components/TagNav";

function Home() {
    const [tag,setTag]= useState('');
    const [token,setToken]= useState('');
    const [classes,setClass]= useState([]);

    const [singleClass,setSingle]=useState({});
    const url = "https://api.trungthanhweb.com/api/";
    const setSingleClass = (id)=>{
        var item= {};
        classes.forEach(el => {
            if(el.id==id){
                item=el;
            }
        });
        setSingle(item);
    }
    useEffect(()=>{
        setTag('Home');
        if(localStorage.getItem('token')){
            setToken(localStorage.getItem('token'))
        }else{
            window.location.replace('/');
        }
    },[])
    useEffect(()=>{
        fetch(url+'getClassListTeacher?token='+token).then(res=>res.json()).then(res=>setClass(res));
    },[token])
  return (
    <>
      <Sidebar />
      <section className="main_content dashboard_part large_header_bg">
        <Menu />
        <div className="main_content_iner overly_inner">
            <TagNav name={tag}/>
            <div className="table-responsive">
  <table className="table table-success">
    <thead>
      <tr>
        <th scope="col">Lá»›p</th>
      </tr>
    </thead>
    <tbody>
        {classes.length>0 && classes.map((item,index)=>(
                  <tr key={index} className>
                  <td scope="row">
                    <a href="#" onClick={(e)=>setSingleClass(item.id)}>{item.name}</a>
                  </td>
                </tr>
        ))}

    </tbody>
  </table>
</div>

            
        </div>
      </section>
    </>
  );
}

export default Home;
